import React, { useState, useEffect } from 'react';
import MySlider from './MySlider';
import { Button, makeStyles } from '@material-ui/core';
import { questionnaires, baseUrl } from '../utils/Constants';

const useStyles = makeStyles({
  root: {
    height: "100%",
    width: "100%",
    display: "grid",
  },
  questionnaireContainer: {
    display: "grid",
    "grid-template-rows": "1fr",
    "grid-template-columns": "3fr 2fr",
  },
  left: {
    gridRow: 1,
    gridColumn: 1,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  right: {
    gridRow: 1,
    gridColumn: 2,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  image: {
    width: "100%",
    rightRow: 1,
    gridColumn: 1,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  sliderContainer: {
    width: "100%",
  },
  button: {
    marginLeft: "1rem",
    height: "2.5rem",
    width: 100,
  },
});

const initialAnswers = questionnaires.map((questionnaire, key)=>{
  return {questionnaireId: questionnaire.id, value: 3}
});

const Questionnaire = () =>  {
  const classes = useStyles();
  const [answers, setAnswers] = useState(Array(questionnaires.length).fill(3));
  const [image, setImage] = useState({id: 999,src: "./1.png"});
  useEffect(() => {
    console.log('useEffect called !', answers);
    console.log('initialAnswers ', initialAnswers);
  });
  const _setAnswers = newAnswers => setAnswers(newAnswers);
  async function getImage() {
    const url = baseUrl+'/image'
    await fetch(url)
      .then(res => res.json())
      .then(json => setImage(json));
  };
  async function postAnswers() {
    // post to server, using fetch etc.
    const data = {
      imageId: image.id,
      answers: answers,
    };
    const url = baseUrl+'/answers'
    const response = await fetch(url, {
      method: 'POST',
      mode: 'cors',
      cache: 'no-cache',
      headers: {'Content-Type': 'application/json'},
      redirect: 'follow',
      referrerPolicy: 'no-referrer',
      body: JSON.stringify(data)
    });
    console.log(response.json());
    // get next image, using fetch etc.
  };
  const handleSubmitClick = () => {
    postAnswers();
    setAnswers(Array(questionnaires.length).fill(3));
    getImage();
  };

  return (
    <div className={classes.root}>
      <div className={classes.questionnaireContainer}>
        <div className={classes.left}>
          <img src={image.src} className={classes.image} alt={image.src}/>
        </div>
        <div className={classes.right}>
          {questionnaires.map((questionnaire, questionnaireIndex)=>
            <div className={classes.sliderContainer} key={questionnaireIndex}>
              <MySlider
                questionnaire={questionnaire}
                questionnaireIndex={questionnaireIndex}
                answers={answers}
                setAnswers={_setAnswers}
              />
            </div>
          )}
        </div>
      </div>
      <Button
        variant="contained"
        color="primary"
        className={classes.button}
        onClick={() => handleSubmitClick()}
      >
        Submit
      </Button>
    </div>
  );
}

export default Questionnaire;
