import React, { useState, useEffect } from 'react';
import MySlider from './MySlider';
import { Button, makeStyles } from '@material-ui/core';
import { BASE_URL } from '../utils/Constants';

const useStyles = makeStyles({
  root: {
    height: "100%",
    width: "100%",
    display: "grid",
  },
  questionnaireContainer: {
    display: "grid",
    "grid-template-rows": "1fr",
    "grid-template-columns": "1fr 1fr",
  },
  left: {
    gridRow: 1,
    gridColumn: 1,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  right: {
    gridRow: 1,
    gridColumn: 2,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  image: {
    width: "100%",
    rightRow: 1,
    gridColumn: 1,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  sliderContainer: {
    width: "100%",
  },
  button: {
    marginLeft: "1rem",
    height: "2.5rem",
    width: 100,
  },
});

let initialAnswers = [];

const Questionnaire = () =>  {
  const classes = useStyles();
  const [answers, setAnswers] = useState([]);
  const [image, setImage] = useState({});
  useEffect(() => {
    getQuestionnaires();
    initialAnswers = QUESTIONNAIRES.map((questionnaire, key)=>{
      return {questionnaireId: questionnaire.id, value: 3}
    });
    setAnswers(initialAnswers);
  });
  const _setAnswers = newAnswers => setAnswers(newAnswers);
  async function getQuestionnaires() {
    const url = BASE_URL+'/questionnaires'
    await fetch(url)
      .then(res => res.json())
      .then(json => {
        QUESTIONNAIRES = json.questionnaires
      });
  };
  async function getImage() {
    const url = BASE_URL+'/image'
    await fetch(url)
      .then(res => res.json())
      .then(json => setImage(json.image));
  };
  async function postAnswers() {
    const data = {
      imageId: image.id,
      answers: answers,
    };
    const url = BASE_URL+'/answers'
    const response = await fetch(url, {
      method: 'POST',
      mode: 'cors',
      cache: 'no-cache',
      headers: {'Content-Type': 'application/json'},
      redirect: 'follow',
      referrerPolicy: 'no-referrer',
      body: JSON.stringify(data)
    });
    console.log(response.json());
  };
  const handleSubmitClick = () => {
    postAnswers();
    setAnswers(initialAnswers);
    getImage();
  };

  return (
    <div className={classes.root}>
      <div className={classes.questionnaireContainer}>
        <div className={classes.left}>
          <img src={image.src} className={classes.image} alt={image.src}/>
        </div>
        <div className={classes.right}>
          {QUESTIONNAIRES.map((questionnaire, questionnaireIndex)=>
            <div className={classes.sliderContainer} key={questionnaireIndex}>
              <MySlider
                questionnaire={questionnaire}
                questionnaireIndex={questionnaireIndex}
                answers={answers}
                setAnswers={_setAnswers}
              />
            </div>
          )}
        </div>
      </div>
      <Button
        variant="contained"
        color="primary"
        className={classes.button}
        onClick={() => handleSubmitClick()}
      >
        Submit
      </Button>
      <Button
        variant="contained"
        color="primary"
        className={classes.button}
        onClick={() => getQuestionnaires()}
      >
       get questionnaires
      </Button>
    </div>
  );
}

export default Questionnaire;
