import React, { useState, useEffect } from 'react';
import MySlider from './MySlider';
import { makeStyles } from '@material-ui/styles';
import logo from '../logo.svg';

const useStyles = makeStyles({
  root: {
    height: '100%',
    width: '100%',
    display: "grid",
  },
  questionnaireContainer: {
    display: "grid",
    "grid-template-rows": "1fr",
    "grid-template-columns": "1fr 1fr",
    marginBottom: "2rem",
  },
  left: {
    rightRow: 1,
    gridColumn: 1,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  right: {
    gridRow: 1,
    gridColumn: 2,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  image: {
    width: "100%",
    rightRow: 1,
    gridColumn: 1,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  sliderContainer: {
    width: 300,
  },
});

const questionnaires = [
  {
    id: 1,
    descriptionRight: '好き',
    descriptionLeft: '嫌い',
  },
  {
    id: 2,
    descriptionRight: '快',
    descriptionLeft: '不快',
  },
  {
    id: 3,
    descriptionRight: '面白い',
    descriptionLeft: '退屈な',
  },
];

const Questionnaire = () =>  {
  const classes = useStyles();
  const [answers, setAnswers] = useState(Array(questionnaires.length).fill(3));
  useEffect(() => {
    console.log('useEffect called!', answers);
  });
  const _setAnswers = (newAnswers) => {
    setAnswers(newAnswers)
  };

  return (
    <div className={classes.root}>
      <p>current answer -> {answers}</p>
      <div className={classes.questionnaireContainer}>
        <div className={classes.left}>
          <img src={logo} className={classes.image}/>
        </div>
        <div className={classes.right}>
          {questionnaires.map((questionnaire, questionnaireIndex)=>
            <div className={classes.sliderContainer} key={questionnaireIndex}>
              <MySlider
                questionnaire={questionnaire}
                questionnaireIndex={questionnaireIndex}
                answers={answers}
                setAnswers={_setAnswers}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Questionnaire;
