import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Slider from '@material-ui/core/Slider';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    // display: 'flex',
    flexDirection: 'row',
  },
}));

const marks = [
  {
    value: 1,
    label: '1',
  },
  {
    value: 2,
    label: '2',
  },
  {
    value: 3,
    label: '3',
  },
  {
    value: 4,
    label: '4',
  },
  {
    value: 5,
    label: '5',
  },
];

const MySlider = ({questionnaireIndex, questionnaire, answers, setAnswers}) => {
  const classes = useStyles();
  // const {questionnaireIndex, questionnaire, answers, setAnswers} = props;
  const _setAnswers = (newAnser) => {
    // console.log(answers);
    answers.splice(questionnaireIndex, 1, newAnser);
    // console.log(answers);
    setAnswers(answers);
  };

  return (
    <div className={classes.root}>
      <p>{questionnaireIndex}</p>
      <p>{answers[questionnaireIndex]}</p>
      <p>{questionnaire.descriptionLeft}</p>
      <div className={classes.sliderContainer}>
        <Slider
          track={false}
          min={1}
          max={5}
          aria-labelledby="track-false-slider"
          defaultValue={3}
          marks={marks}
          value={answers[questionnaireIndex]}
          onChange={(_, value)=>_setAnswers(value)}
        />
      </div>
      <p>{questionnaire.descriptionRight}</p>
    </div>
  );
}

export default MySlider
