import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Slider from '@material-ui/core/Slider';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    // display: 'flex',
    flexDirection: 'row',
  },
}));

const marks = [
  {
    value: 1,
    label: '1',
  },
  {
    value: 2,
    label: '2',
  },
  {
    value: 3,
    label: '3',
  },
  {
    value: 4,
    label: '4',
  },
  {
    value: 5,
    label: '5',
  },
];

const MySlider = (props) => {
  const classes = useStyles();
  const {questionnaireIndex, questionnaire, answers, setAnswers} = props;
  const _setAnsers = (newAnser) => {
    answers[questionnaireIndex] = newAnser;
    setAnswers(answers);
  };

  return (
    <div className={classes.root}>
      <p>{questionnaire.descriptionLeft}</p>
      <div className={classes.sliderContainer}>
        <Slider
          track={false}
          min={1}
          max={5}
          aria-labelledby="track-false-slider"
          defaultValue={3}
          marks={marks}
          value={answers[questionnaireIndex]}
          onChange={(_, value)=>_setAnsers(value)}
        />
      </div>
      <p>{questionnaire.descriptionRight}</p>
    </div>
  );
}

export default MySlider
